  difficulty,
  tier,
  active,
  created_at,
  updated_at
) VALUES (
  'saa-c03-design_secure_applications_architectures-104-br',
  'SAA-C03',
  'SECURE',
  'Uma empresa está implantando uma nova aplicação de negócios na AWS. A aplicação é executada em duas instâncias EC2 e usa um bucket S3 para armazenamento de documentos. Um arquiteto de soluções deve garantir que as instâncias EC2 possam acessar o bucket S3. O que deve ser feito?',
  'Criar uma função IAM que conceda acesso ao bucket S3 e anexá-la às instâncias EC2.',
  'Criar uma política IAM que conceda acesso ao bucket S3 e anexá-la diretamente às instâncias EC2.',
  'Criar um grupo IAM com acesso ao bucket S3 e anexá-lo às instâncias EC2.',
  'Criar um usuário IAM com acesso ao bucket S3 e anexar suas credenciais às instâncias EC2.',
  '',
  '["A"]'::jsonb,
  1,
  '',
  'Anexar uma função IAM é como dar às suas instâncias EC2 crachás especiais que lhes permitem acessar apenas o que precisam, sem compartilhar credenciais sensíveis.',
  '{"B":"Você не pode anexar uma política IAM diretamente a uma instância EC2; as políticas devem ser anexadas a identidades como funções ou usuários.","C":"Os grupos IAM são usados para agrupar usuários IAM, não para serem anexados a instâncias EC2.","D":"Incorporar credenciais de usuário de longo prazo em instâncias é inseguro e aumenta a sobrecarga operacional para rotação."}'::jsonb,
  'medium',
  'FREE',
  true,
  NOW(),
  NOW()
) ON CONFLICT (id) DO NOTHING;
INSERT INTO public.questions (
