  updated_at
) VALUES (
  'aif-c01-ai_services-099-br',
  'AIF-C01',
  'AI_SERVICES',
  'Qual serviço da AWS converte texto em fala realista?',
  'Amazon Comprehend',
  'Amazon Translate',
  'Amazon Transcribe',
  'Amazon Polly',
  '',
  '["D"]'::jsonb,
  1,
  '',
  'O Amazon Polly é um serviço neural de conversão de texto em fala que fornece vozes naturais e controle SSML sobre prosódia, pronúncia e ênfase. Ele suporta vários idiomas e pode transmitir áudio para aplicações de baixa latência. O Comprehend realiza análises de PNL, o Translate lida com a tradução de idiomas e o Transcribe converte fala em texto. O Polly é a solução TTS correta na AWS.',
  '{"A":"O Comprehend analisa o texto, mas não sintetiza a fala.","B":"O Translate converte texto entre idiomas, não para áudio.","C":"O Transcribe é de fala para texto, o inverso do TTS."}'::jsonb,
  'fácil',
  'GRATUITO',
  true,
  NOW(),
  NOW()
) ON CONFLICT (id) DO NOTHING;
INSERT INTO public.questions (
  id,
  certification_id,
  domain,
