) VALUES (
  'aif-c01-responsible_ai-164-br',
  'AIF-C01',
  'RESPONSIBLE_AI',
  'Qual ferramenta pode ser usada para documentar informações do modelo para transparência?',
  'Amazon SageMaker Model Cards',
  'Amazon EC2',
  'Amazon S3',
  'Amazon RDS',
  '',
  '["A"]'::jsonb,
  1,
  '',
  'Os SageMaker Model Cards consolidam o propósito, dados de treinamento, métricas, avaliações de viés/robustez, riscos e recomendações de uso, promovendo transparência e governança. Integre com o Clarify para anexar resultados de justiça/explicabilidade e com o Model Registry para versionamento e trilha de auditoria. Armazene artefatos no S3 e monitore em produção com o Model Monitor. EC2, S3 e RDS são úteis para infraestrutura, mas não substituem a documentação padronizada do ciclo de vida do modelo.',
  '{"B":"O EC2 fornece computação; não é uma ferramenta de documentação de modelos.","C":"O S3 armazena artefatos, mas não estrutura os model cards.","D":"O RDS é um banco de dados; não fornece os model cards."}'::jsonb,
  'médio',
  'GRATUITO',
  true,
  NOW(),
  NOW()
) ON CONFLICT (id) DO NOTHING;
INSERT INTO public.questions (
  id,
  certification_id,
  domain,
  question_text,
  option_a,
  option_b,
  option_c,
